import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-popup-ad',
  standalone: true,
  templateUrl: './popup-ad.component.html',
  imports: [CommonModule],
})
export class PopupAdComponent implements OnInit {
  showAd = false;
  isVisible = false;
  adImage = 'assets/Five_For_Five.png';
  adLink = 'https://chat.whatsapp.com/H0Dh9vfvzoeLwwRI8kmWi4';

  ngOnInit() {
    // Show after 3 seconds
    setTimeout(() => {
      this.showAd = true;

      // Wait a bit to trigger fade/scale animation
      setTimeout(() => {
        this.isVisible = true;
      }, 50);
    }, 3000);
  }

  closeAd() {
    // Start fade-out
    this.isVisible = false;
    // Remove element after animation
    setTimeout(() => (this.showAd = false), 400);
  }
}
<div 
  *ngIf="showAd" 
  class="fixed inset-0 bg-black/50 flex items-center justify-center z-50 transition-opacity duration-500"
  [ngClass]="{ 'opacity-100': isVisible, 'opacity-0': !isVisible }"
>
  <div 
    class="bg-white rounded-lg shadow-xl max-w-sm w-full p-4 relative transform transition-all duration-500"
    [ngClass]="{ 'scale-100 opacity-100': isVisible, 'scale-75 opacity-0': !isVisible }"
  >
    <button 
      (click)="closeAd()" 
      class="absolute top-2 right-2 text-gray-500 hover:text-gray-700"
    >
      ✕
    </button>

    <a [href]="adLink" target="_blank" rel="noopener noreferrer">
      <img 
        [src]="adImage" 
        alt="Popup Ad" 
        class="rounded-lg w-full object-cover"
      />
    </a>
  </div>
</div>
