import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-popup-ad',
  standalone: true,
  templateUrl: './popup-ad.component.html',
  imports: [CommonModule],
})
export class PopupAdComponent implements OnInit {
  showAd = false;
  adImage = 'assets/Five_For_Five.png'; // your ad image
  adLink = 'https://chat.whatsapp.com/H0Dh9vfvzoeLwwRI8kmWi4';

  ngOnInit() {
    // Show after 3 seconds
    setTimeout(() => {
      this.showAd = true;
    }, 3000);
  }

  closeAd() {
    this.showAd = false;
  }
}
<div 
  *ngIf="showAd" 
  class="fixed inset-0 bg-black/50 flex items-center justify-center z-50"
>
  <div class="bg-white rounded-lg shadow-lg max-w-sm w-full p-4 relative">
    <button 
      (click)="closeAd()" 
      class="absolute top-2 right-2 text-gray-500 hover:text-gray-700"
    >
      ✕
    </button>
    <a [href]="adLink" target="_blank" rel="noopener noreferrer">
      <img 
        [src]="adImage" 
        alt="Popup Ad" 
        class="rounded-lg w-full object-cover"
      />
    </a>
  </div>
</div>
